var MobileNav = {

	toggleNav: function() {
		$('nav').slideToggle();
	},
	
	registerHamburgerIconForTaps: function() {
		$('.hamburger-container').click(function(e){
			MobileNav.toggleNav();
			e.stopPropagation();
		});
	}
};


$(document).ready(function() {
	MobileNav.registerHamburgerIconForTaps()
});
